<script>
document.addEventListener('DOMContentLoaded', () => {
  try {
    // DOM Elements
    const qrImage = document.getElementById('qr-image');
    const generateBtn = document.getElementById('generate-btn');
    const downloadBtns = document.querySelectorAll('.download-btn');
    const optionCards = document.querySelectorAll('.option-card');
    const formSections = document.querySelectorAll('.form-section');
    const sizeSlider = document.getElementById('qr-size');
    const sizeValue = document.getElementById('size-value');
    const logoInput = document.getElementById('logo-input');
    const logoPreview = document.getElementById('logo-preview');
    const logoUpload = document.getElementById('logo-upload');
    const logoUploadText = document.getElementById('logo-upload-text');
    const logoSizeSelect = document.getElementById('logo-size');
    const transparentBgCheckbox = document.getElementById('transparent-bg');
    
    // Color picker elements
    const fgColorPicker = document.getElementById('fg-color');
    const bgColorPicker = document.getElementById('bg-color');
    const fgHexInput = document.getElementById('fg-hex');
    const bgHexInput = document.getElementById('bg-hex');
    const fgPreview = document.getElementById('fg-preview');
    const bgPreview = document.getElementById('bg-preview');
    
    // Current QR code settings
    let currentType = 'url';
    let logoFile = null;
    
    // Initialize
    updateSizeDisplay();
    setupEventListeners();
    
    function setupEventListeners() {
      // QR type selection
      optionCards.forEach(card => {
        card.addEventListener('click', () => {
          optionCards.forEach(c => c.classList.remove('active'));
          card.classList.add('active');
          currentType = card.dataset.type;
          
          // Show the corresponding form section
          formSections.forEach(section => {
            section.classList.remove('active');
            if (section.id === `${currentType}-form`) {
              section.classList.add('active');
            }
          });
        });
      });
      
      // Generate button
      generateBtn.addEventListener('click', generateQRCode);
      
      // Download buttons
      downloadBtns.forEach(btn => {
        btn.addEventListener('click', () => downloadQRCode(btn.dataset.format));
      });
      
      // Size slider
      sizeSlider.addEventListener('input', updateSizeDisplay);
      
      // Logo upload
      logoInput.addEventListener('change', handleLogoUpload);
      logoUpload.addEventListener('click', () => logoInput.click());
      
      // Color pickers
      fgColorPicker.addEventListener('input', updateColorFromPicker);
      bgColorPicker.addEventListener('input', updateColorFromPicker);
      fgHexInput.addEventListener('input', updateColorFromHex);
      bgHexInput.addEventListener('input', updateColorFromHex);
    }
    
    function updateSizeDisplay() {
      sizeValue.textContent = sizeSlider.value;
    }
    
    function updateColorFromPicker(e) {
      const color = e.target.value;
      if (e.target.id === 'fg-color') {
        fgHexInput.value = color;
        fgPreview.style.backgroundColor = color;
      } else {
        bgHexInput.value = color;
        bgPreview.style.backgroundColor = color;
      }
    }
    
    function updateColorFromHex(e) {
      const hexInput = e.target.value;
      // Validate hex color
      if (/^#[0-9A-F]{6}$/i.test(hexInput)) {
        if (e.target.id === 'fg-hex') {
          fgColorPicker.value = hexInput;
          fgPreview.style.backgroundColor = hexInput;
        } else {
          bgColorPicker.value = hexInput;
          bgPreview.style.backgroundColor = hexInput;
        }
      }
    }
    
    function handleLogoUpload(e) {
      const file = e.target.files[0];
      if (!file) return;
      
      // Validate file type and size
      if (!['image/jpeg', 'image/png'].includes(file.type)) {
        alert('Please upload a JPEG or PNG image.');
        return;
      }
      
      if (file.size > 500 * 1024) {
        alert('File size should be less than 500KB.');
        return;
      }
      
      logoFile = file;
      
      // Preview the logo
      const reader = new FileReader();
      reader.onload = (event) => {
        logoPreview.src = event.target.result;
        logoPreview.style.display = 'block';
        logoUploadText.style.display = 'none';
      };
      reader.readAsDataURL(file);
    }
    
    function getQRContent() {
      switch (currentType) {
        case 'url':
          return document.getElementById('url-input').value;
        case 'text':
          return document.getElementById('text-input').value;
        case 'contact':
          return generateVCard();
        case 'wifi':
          return generateWifiConfig();
        default:
          return '';
      }
    }
    
    function generateVCard() {
      const name = document.getElementById('contact-name').value || '';
      const company = document.getElementById('contact-company').value || '';
      const phone = document.getElementById('contact-phone').value || '';
      const email = document.getElementById('contact-email').value || '';
      const address = document.getElementById('contact-address').value || '';
      const website = document.getElementById('contact-website').value || '';
      
      let vcard = 'BEGIN:VCARD\nVERSION:3.0\n';
      if (name) vcard += `FN:${name}\nN:${name.split(' ').reverse().join(';')};;;\n`;
      if (company) vcard += `ORG:${company}\n`;
      if (phone) vcard += `TEL:${phone}\n`;
      if (email) vcard += `EMAIL:${email}\n`;
      if (address) vcard += `ADR:;;${address.replace(/\n/g, ';')}\n`;
      if (website) vcard += `URL:${website}\n`;
      vcard += 'END:VCARD';
      
      return vcard;
    }
    
    function generateWifiConfig() {
      const ssid = document.getElementById('wifi-ssid').value || '';
      const password = document.getElementById('wifi-password').value || '';
      const encryption = document.getElementById('wifi-type').value;
      const hidden = document.getElementById('wifi-hidden').value === 'true';
      
      let config = `WIFI:S:${ssid};`;
      if (password) config += `P:${password};`;
      if (encryption) config += `T:${encryption};`;
      if (hidden) config += `H:true;`;
      config += ';';
      
      return config;
    }
    
    function generateQRCode() {
      const content = getQRContent();
      if (!content && currentType !== 'text') {
        alert('Please enter content for the QR code');
        return;
      }
      
      // Base QR code URL
      let qrUrl = 'https://api.qrserver.com/v1/create-qr-code/?';
      
      // Size
      const size = sizeSlider.value;
      qrUrl += `size=${size}x${size}&`;
      
      // Data
      qrUrl += `data=${encodeURIComponent(content)}&`;
      
      // Colors
      const fgColor = fgHexInput.value.replace('#', '');
      const bgColor = transparentBgCheckbox.checked ? 'ffffff00' : bgHexInput.value.replace('#', '');
      qrUrl += `color=${fgColor}&bgcolor=${bgColor}&`;
      
      // Margin
      qrUrl += 'margin=10&';
      
      // Format (always PNG for preview)
      qrUrl += 'format=png';
      
      // Add logo if uploaded
      if (logoFile) {
        qrUrl += `&logo=${encodeURIComponent(logoPreview.src)}`;
        const logoSize = Math.floor(size * parseFloat(logoSizeSelect.value));
        qrUrl += `&logosize=${logoSize}`;
      }
      
      // Update QR code image
      qrImage.src = qrUrl;
      
      // Show download buttons
      downloadBtns.forEach(btn => btn.disabled = false);
    }
    
    function downloadQRCode(format) {
      const content = getQRContent();
      if (!content && currentType !== 'text') {
        alert('Please generate a QR code first');
        return;
      }
      
      // Base QR code URL
      let qrUrl = 'https://api.qrserver.com/v1/create-qr-code/?';
      
      // Size
      const size = sizeSlider.value;
      qrUrl += `size=${size}x${size}&`;
      
      // Data
      qrUrl += `data=${encodeURIComponent(content)}&`;
      
      // Colors
      const fgColor = fgHexInput.value.replace('#', '');
      let bgColor = bgHexInput.value.replace('#', '');
      if (transparentBgCheckbox.checked && format === 'png') {
        bgColor = 'ffffff00'; // Transparent background
      }
      qrUrl += `color=${fgColor}&bgcolor=${bgColor}&`;
      
      // Margin
      qrUrl += 'margin=10&';
      
      // Format
      qrUrl += `format=${format}`;
      
      // Add logo if uploaded and format is png
      if (logoFile && format === 'png') {
        qrUrl += `&logo=${encodeURIComponent(logoPreview.src)}`;
        const logoSize = Math.floor(size * parseFloat(logoSizeSelect.value));
        qrUrl += `&logosize=${logoSize}`;
      }
      
      // Create download link
      const link = document.createElement('a');
      link.href = qrUrl;
      link.download = `qr-code-${currentType}-${new Date().getTime()}.${format}`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
    
    // Initialize with a default QR code
    generateQRCode();
    
  } catch (error) {
    console.error('Error initializing QR code generator:', error);
  }
});
</script>